//! Text.zig
//! Custom lo-fi text rendering from a bitmap

const std = @import("std");
const print = std.debug.print;
const assert = std.debug.assert;
const ascii = std.ascii;
const Gui = @import("Gui.zig");
const Rect = Gui.Recti;

// Copy-paste this into your code
// Generated by https://github.com/tsoding/domsson-fontgen from `./charmap-oldschool_white.png`
pub const BMP_WIDTH = 128;
pub const BMP_HEIGHT = 64;

pub const ASCII_MIN = 32;
pub const ASCII_MAX = 127;

pub const ROWS = 7;
pub const COLUMNS = 18;

pub const GLYPH_WIDTH = BMP_WIDTH / COLUMNS;
pub const GLYPH_HEIGHT = BMP_HEIGHT / ROWS;

pub fn drawText(
    text: []const u8,
    bits: [*]u32,
    rect: Rect,
    scale: f32,
    color: u32,
) void {
    const ws: u32 = @intFromFloat(@round(GLYPH_WIDTH * scale));
    const hs: u32 = @intFromFloat(@round(GLYPH_HEIGHT * scale));
    var x = rect.x;
    const y = rect.y;
    const iscale: u32 = @intFromFloat(scale);

    for (text) |c| {
        if (c < ASCII_MIN or c > ASCII_MAX) {
            continue;
        }
        const glyph_rect = Rect.intersection(rect, .{
            .x = x,
            .y = y,
            .width = ws,
            .height = hs,
        });
        const glyph = getGlyph(c);
        for (glyph_rect.y..glyph_rect.y + glyph_rect.height) |line| {
            const bits_line = bits[line * Gui.GUI_WIDTH + glyph_rect.x ..];
            const glyph_line_offset = ((line - y) / iscale) * BMP_WIDTH;
            const glyph_line_end = glyph_line_offset + GLYPH_WIDTH;
            const glyph_line = glyph[glyph_line_offset..glyph_line_end];
            for (glyph_rect.x..glyph_rect.x + glyph_rect.width) |col| {
                if (glyph_line[(col - x) / iscale] == 1) bits_line[col] = color;
            }
        }

        x += ws;
    }
}

test "Image draw" {
    const string = "Text";
    for (string) |c| {
        const glyph = getGlyph(c);
        for (0..GLYPH_HEIGHT) |y| {
            const glyph_line = glyph[y * BMP_WIDTH .. y * BMP_WIDTH + GLYPH_WIDTH];
            for (0..GLYPH_WIDTH) |x| {
                if (glyph_line[x] == 1) print("*", .{}) else print(" ", .{});
            }
            print("\n", .{});
        }
    }
}

/// get glyph as slice into font pixels
pub fn getGlyph(c: u8) []const u1 {
    assert(!ascii.isControl(c));
    const index: usize = @intCast(c - ASCII_MIN);
    const row = index / COLUMNS;
    const col = index % COLUMNS;

    return font[(row * GLYPH_HEIGHT * BMP_WIDTH) + (col * GLYPH_WIDTH) ..];
}

